select count(*) from supplier

  		   and sup_id = #{supId} 
  		   and sup_name = #{supName} 
  		   and description = #{description} 
  		   and status = #{status} 
  		   and org_id = #{orgId} 
  		   and sc_id = #{scId} 
  		   and address = #{address} 
  		   and legal_person = #{legalPerson} 
  		   and phone = #{phone} 
  		   and linkman = #{linkman} 
  		   and email = #{email} 
  		   and social_credit_code = #{socialCreditCode} 
  		   and registered_assets = #{registeredAssets} 
  		   and floor_area = #{floorArea} 
  		   and date_establishment = #{dateEstablishment} 
  		   and business_volume = #{businessVolume} 
  		   and bank_credit_status = #{bankCreditStatus} 
  		   and human_resource_situation = #{humanResourceSituation} 
  		   and status_equipment = #{statusEquipment} 
  		   and business_scope = #{businessScope} 
  		   and product_serve = #{productServe} 
  		   and articles_agreement = #{articlesAgreement} 
  		   and create_time = #{createTime} 
  		   and update_time = #{updateTime}

select `sup_id`,`sup_name`,supplier.org_id,`address`,`legal_person`,`phone` ,`linkman`,`email`,`social_credit_code`,`registered_assets` ,`floor_area`,`date_establishment`,`business_volume`,`bank_credit_status`,`human_resource_situation` ,`status_equipment`,`business_scope`,`product_serve`,`articles_agreement`,supplier.create_time ,sc.sc_id,sc.sc_name from supplier LEFT JOIN supplier_category sc ON supplier.sc_id=sc.sc_id INNER JOIN t_org_info o on o.id=supplier.org_id
and o.create_by=#{userId}
where 1=1
and supplier.sup_name LIKE "%"#{input}"%"
ORDER BY supplier.create_time desc
limit #{start},#{size}

select `sup_id`,`sup_name`,`address`,`legal_person`,`phone` ,`linkman`,`email`,`social_credit_code`,`registered_assets` ,`floor_area`,`date_establishment`,`business_volume`,`bank_credit_status`,`human_resource_situation` ,`status_equipment`,`business_scope`,`product_serve`,`articles_agreement`,supplier.create_time ,sc.sc_id,sc.sc_name from supplier LEFT JOIN supplier_category sc ON supplier.sc_id=sc.sc_id where 1=1
and supplier.org_id=#{id}
limit 0,1

select count(0) from t_deli_inst_man
where 1=1

	and type=#{type}


	and supplier_id=#{supplier_id}


	and ((`name` LIKE "%"#{input}"%")or(`phone_num` LIKE "%"#{input}"%"))

	select id,user_id,supplier_id,`name`,dim_no,state
,type,sex,phone_num,create_time,update_time
from t_deli_inst_man
	where 1=1

		and type=#{type}


		and supplier_id=#{supplier_id}


		and ((`name` LIKE "%"#{input}"%")or(`phone_num` LIKE "%"#{input}"%"))

	ORDER BY create_time desc

		limit #{start},#{size}

select*from t_deli_inst_man where 1=1

	and id=#{id}


	and type=#{type}

limit 0,1

select count(0)from goods g LEFT JOIN goods_category gc3 on gc3.cid=g.cid LEFT JOIN goods_category gc2 on gc2.cid=gc3.parent_cid and gc2.lev<gc3.lev LEFT JOIN goods_category gc1 on gc1.cid=gc2.parent_cid and gc1.lev<gc2.lev where 1=1
and g.sup_id=#{sup_id}
and ((g.goods_name LIKE "%"#{input}"%")or(g.goods_num LIKE "%"#{input}"%"))

select g.goods_id,g.goods_num,g.goods_name,g.create_time,g.`status`,g.cid ,gc3.c_name gc3_c_name,gc2.c_name gc2_c_name,gc1.c_name gc1_c_name from goods g LEFT JOIN goods_category gc3 on gc3.cid=g.cid LEFT JOIN goods_category gc2 on gc2.cid=gc3.parent_cid and gc2.lev<gc3.lev LEFT JOIN goods_category gc1 on gc1.cid=gc2.parent_cid and gc1.lev<gc2.lev where 1=1
and g.sup_id=#{sup_id}
and ((g.goods_name LIKE "%"#{input}"%")or(g.goods_num LIKE "%"#{input}"%"))
limit #{start},#{size}

select*from t_order where 1=1
and id=#{id}
limit 0,1

select*from supplier s where 1=1
and s.org_id=#{orgId}
limit 0,1

select `id`,`order_no`,`create_by`,reviewer_id,approver_id ,`org_id`,`status`,`money`,`approval_opinion`,`audit_opinion` ,`parent_id`,audit_time,`create_time`,`update_time`,supplier_id ,refuseCause,refuse,isUseful from t_order where 1=1
and id=#{id}
limit 0,1

select goods.goods_name attr_value from goods
where goods.goods_id = #{goodsId}
union
select av.attr_value from sku_value_ref svr
left join attr_value av on av.av_id = svr.av_id
where svr.sku_id = #{skuId}
union
select av.attr_value from goods_value_ref gvr
left join attr_value av on av.av_id = gvr.av_id
where gvr.goods_id = #{goodsId}
